class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let a=parseInt($(e).attr("data-likes"));1==t.data.deleted?a-=1:a+=1,$(e).attr("data-likes",a),0==t.data.deleted?$(e).html(a+' <i class="fas fa-thumbs-up"></i>'):$(e).html(a+' <i class="far fa-thumbs-up"></i>')})).fail((function(t){console.log("Error in creating ajax for likes",t)}))}))}}